import React ,{Component} from 'react';
import { FaStar } from "react-icons/fa";

class MovieCard extends Component{
    
    render(){
        return(
            <div className="card" style={{width:"100%"}}>
                <div className="card-header" style={{  height: '280px' }}>
                <img alt="" src={this.props.img} style={{ width: '100%', height: '170px' ,borderRadius:'10px'}}/>
                </div>
                <div className="card-body" style={{ height: '110px',display:"flex",justifyContent:"space-between" }}>
                    <p  style={{color:"white"}}><span style={{color:"white" , fontWeight:"800"}} >{this.props.title}</span><br></br><span style={{color:"gray"}} >{this.props.genre}</span></p>
                    <ul style={{color:"white",listStyleType:"none"}}><li><FaStar style={{ color: "#ede607"}}/>{this.props.rate} </li></ul><br />
                </div>
            </div>
        )
    }
}
export default MovieCard;
